import React, { useState, useRef } from "react";
import {
  Modal,
  Text,
  StyleSheet,
  View,
  TextInput,
  ScrollView,
  Pressable,
} from "react-native";
import DateTimePicker from "react-native-ui-datepicker";
import { Form, FormItem } from "react-native-form-component";
const Formulario = ({ modalVisible, setModalVisible }) => {
  const [fecha, setFecha] = useState(new Date());
  const [paciente, setPaciente] = useState("");
  const [nombrePropietario, setPropietario] = useState("");
  const [email, setEmail] = useState("");
  const [telefono, setTelefono] = useState("");
  const [sintomas, setSintomas] = useState("");

  const pacienteInput = useRef();
  const propietarioInput = useRef();
  const emailInput = useRef();
  const telefonoInput = useRef();

  const handleAgregarPaciente = () => {
    // ... (lógica de agregar paciente)
  };

  const handleValidation = (fieldName, value) => {
    switch (fieldName) {
      case "paciente":
      case "propietario":
        return /^[A-Za-z ]{1,50}$/.test(value)
          ? null
          : "Solo se permiten letras y máximo 50 caracteres";
      case "email":
        return /\S+@\S+\.\S+/.test(value) ? null : "Correo electrónico no válido";
      case "telefono":
        return /^\d{10}$/.test(value) ? null : "Número de teléfono no válido";
      case "sintomas":
        return /^[A-Za-z ]{1,100}$/.test(value)
          ? null
          : "Solo se permiten letras y máximo 100 caracteres";
      default:
        return null;
    }
  };

  return (
    <Modal animationType="slide" visible={modalVisible}>
      <View style={styles.contenido}>
        <ScrollView>
          {/* ... (código restante) */}

          <Form onButtonPress={handleAgregarPaciente}>
            <FormItem
              ref={pacienteInput}
              label="Nombre del paciente"
              isRequired
              value={paciente}
              onChangeText={(value) => setPaciente(value)}
              customValidation={() => handleValidation("paciente", paciente)}
            />
            <FormItem
              ref={propietarioInput}
              label="Nombre del propietario"
              isRequired
              value={nombrePropietario}
              onChangeText={(value) => setPropietario(value)}
              customValidation={() => handleValidation("propietario", nombrePropietario)}
            />
            <FormItem
              ref={emailInput}
              label="Email propietario"
              isRequired
              keyboardType="email-address"
              value={email}
              onChangeText={(value) => setEmail(value)}
              customValidation={() => handleValidation("email", email)}
            />
            <FormItem
              ref={telefonoInput}
              label="Telefono propietario"
              isRequired
              keyboardType="number-pad"
              value={telefono}
              onChangeText={(value) => setTelefono(value)}
              customValidation={() => handleValidation("telefono", telefono)}
            />
            <FormItem
              label="Fecha"
              value={fecha}
              renderInput={(props) => (
                <View style={styles.FechaContenedor}>
                  <DateTimePicker
                    {...props}
                    locale="es"
                    mode="date"
                    onValueChange={(date) => setFecha(date)}
                  />
                </View>
              )}
            />
            <FormItem
              label="Síntomas"
              value={sintomas}
              onChangeText={(value) => setSintomas(value)}
              multiline
              rows={4}
              customValidation={() => handleValidation("sintomas", sintomas)}
            />

            <Pressable style={styles.btnNuevaCita}>
              <Text style={styles.btnNuevaCitaTexto}>Agregar paciente</Text>
            </Pressable>
          </Form>
        </ScrollView>
      </View>
    </Modal>
  );
};

const styles = StyleSheet.create({
  // ... (resto del código sigue igual)

  errorText: {
    color: "red",
    fontSize: 12,
  },
  contenido: {
    backgroundColor: "#6D28D9",
    flex: 1,
  },
  titulo: {
    fontSize: 30,
    fontWeight: "600",
    textAlign: "center",
    marginTop: 30,
    color: "#fff",
  },
  tituloBold: {
    fontWeight: "900",
  },
  campo: {
    marginTop: 10,
    marginHorizontal: 30,
  },
  label: {
    color: "#FFF",
    marginBottom: 10,
    marginTop: 15,
    fontSize: 20,
    fontWeight: "600",
  },
  input: {
    backgroundColor: "#FFF",
    padding: 15,
    borderRadius: 10,
  },
  FechaContenedor: {
    backgroundColor: "#FFF",
    borderRadius: 10,
  },
  SintomasInput: {
    height: 100,
  },
  btnCancelar: {
    marginVertical: 30,
    backgroundColor: "#5827A4",
    marginHorizontal: 30,
    padding: 15,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#FFF",
  },
  btnCancelarTexto: {
    color: "#FFF",
    textAlign: "center",
    fontWeight: "900",
    fontSize: 16,
    textTransform: "uppercase",
  },
  btnNuevaCita: {
    marginVertical: 50,
    backgroundColor: "#F59E0B",
    paddingVertical: 10,
    paddingHorizontal: 30,
    borderRadius: 10,
  },
  btnNuevaCitaTexto: {
    textAlign: "center",
    color: "#5827A4",
    textTransform: "uppercase",
    fontWeight: "900",
    fontSize: 16,
  },
});

export default Formulario;
