import React, { useState, useRef } from "react";
import {
  Modal,
  Text,
  StyleSheet,
  View,
  TextInput,
  ScrollView,
  Pressable,
} from "react-native";
import DateTimePicker from "react-native-ui-datepicker";
import { Form, FormItem } from "react-native-form-component";

const Formulario = ({ modalVisible, setModalVisible }) => {
  const [fecha, setFecha] = useState(new Date());
  const [paciente, setPaciente] = useState("");
  const [nombrePropietario, setPropietario] = useState("");
  const [email, setEmail] = useState("");
  const [telefono, setTelefono] = useState("");
  const [sintomas, setSintomas] = useState("");

  const pacienteInput = useRef();
  const propietarioInput = useRef();
  const emailInput = useRef();
  const telefonoInput = useRef();

  const [validationErrors, setValidationErrors] = useState({
    paciente: "",
    propietario: "",
    email: "",
    telefono: "",
    sintomas: "",
  });

  const handleValidation = (fieldName, value) => {
    switch (fieldName) {
      case "paciente":
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          paciente: /^[A-Za-z ]{1,50}$/.test(value)
            ? ""
            : "Solo se permiten letras y máximo 50 caracteres",
        }));
        break;
      case "propietario":
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          propietario: /^[A-Za-z ]{1,50}$/.test(value)
            ? ""
            : "Solo se permiten letras y máximo 50 caracteres",
        }));
        break;
      case "email":
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          email: /\S+@\S+\.\S+/.test(value)
            ? ""
            : "Correo electrónico no válido",
        }));
        break;
      case "telefono":
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          telefono: /^\d{10}$/.test(value)
            ? ""
            : "Número de teléfono no válido",
        }));
        break;
      case "sintomas":
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          sintomas: /^[A-Za-z ]{1,100}$/.test(value)
            ? ""
            : "Solo se permiten letras y máximo 100 caracteres",
        }));
        break;
      default:
        break;
    }
  };  

  const handleAgregarPaciente = () => {
    // Lógica para agregar el paciente

    // Cerrar el modal después de agregar el paciente
    setModalVisible(false);
  };

  return (
    <Modal animationType="slide" visible={modalVisible}>
      <View style={styles.contenido}>
        <Text style={styles.errorText}>{validationErrors.paciente}</Text>
        <ScrollView>
          <Text style={styles.titulo}>
            <Text style={styles.tituloBold}>Nueva Cita</Text>
          </Text>

          <Pressable
            style={styles.btnCancelar}
            onPress={() => setModalVisible(!modalVisible)}
          >
            <Text style={styles.btnCancelarTexto}>Cancelar</Text>
          </Pressable>

          <Form onButtonPress={handleAgregarPaciente}>
            <FormItem
              ref={pacienteInput}
              label="Nombre del paciente"
              isRequired
              value={paciente}
              onChangeText={(value) => {
                setPaciente(value);
                handleValidation("paciente", value);
              }}
              customValidation={() => validationErrors.paciente}
            /><Text style={styles.errorText}>{validationErrors.paciente}</Text>

            <FormItem
              ref={propietarioInput}
              label="Nombre del propietario"
              isRequired
              value={nombrePropietario}
              onChangeText={(value) => {
                setPropietario(value);
                handleValidation("propietario", value);
              }}
              customValidation={() => validationErrors.propietario}
            />
            <FormItem
              ref={emailInput}
              label="Email propietario"
              isRequired
              keyboardType="email-address"
              value={email}
              onChangeText={(value) => {
                setEmail(value);
                handleValidation("email", value);
              }}
              customValidation={() => validationErrors.email}
            />
            <FormItem
              ref={telefonoInput}
              label="Telefono propietario"
              isRequired
              keyboardType="number-pad"
              value={telefono}
              onChangeText={(value) => {
                setTelefono(value);
                handleValidation("telefono", value);
              }}
              customValidation={() => validationErrors.telefono}
            />
            <FormItem
              label="Fecha"
              value={fecha}
              renderInput={(props) => (
                <DateTimePicker
                  {...props}
                  locale="es"
                  mode="date"
                  onValueChange={(date) => setFecha(date)}
                />
              )}
            />
            <FormItem
              label="Síntomas"
              value={sintomas}
              onChangeText={(value) => {
                setSintomas(value);
                handleValidation("sintomas", value);
              }}
              multiline
              rows={4}
              customValidation={() => validationErrors.sintomas}
            />

            <Pressable style={styles.btnNuevaCita}>
              <Text style={styles.btnNuevaCitaTexto}>Agregar paciente</Text>
            </Pressable>
          </Form>
        </ScrollView>
      </View>
    </Modal>
  );
};  

const styles = StyleSheet.create({
  // ... (resto del código sigue igual)

  errorText: {
    color: "red",
    fontSize: 12,
  },
  contenido: {
    backgroundColor: "#6D28D9",
    flex: 1,
  },
  titulo: {
    fontSize: 30,
    fontWeight: "600",
    textAlign: "center",
    marginTop: 30,
    color: "#fff",
  },
  tituloBold: {
    fontWeight: "900",
  },
  campo: {
    marginTop: 10,
    marginHorizontal: 30,
  },
  label: {
    color: "#FFF",
    marginBottom: 10,
    marginTop: 15,
    fontSize: 20,
    fontWeight: "600",
  },
  input: {
    backgroundColor: "#FFF",
    padding: 15,
    borderRadius: 10,
  },
  FechaContenedor: {
    backgroundColor: "#FFF",
    borderRadius: 10,
  },
  SintomasInput: {
    height: 100,
  },
  btnCancelar: {
    marginVertical: 30,
    backgroundColor: "#5827A4",
    marginHorizontal: 30,
    padding: 15,
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#FFF",
  },
  btnCancelarTexto: {
    color: "#FFF",
    textAlign: "center",
    fontWeight: "900",
    fontSize: 16,
    textTransform: "uppercase",
  },
  btnNuevaCita: {
    marginVertical: 50,
    backgroundColor: "#F59E0B",
    paddingVertical: 10,
    paddingHorizontal: 30,
    borderRadius: 10,
  },
  btnNuevaCitaTexto: {
    textAlign: "center",
    color: "#5827A4",
    textTransform: "uppercase",
    fontWeight: "900",
    fontSize: 16,
  },
});

export default Formulario;
